import React, { useEffect, useState } from "react";
import Chrono from "./Chrono";

const Dashboard = () => {
    const [userStats, setUserStats] = useState(null);

    useEffect(() => {
        // Remplace l'ID utilisateur par celui de ton backend
        const userId = "uniqueUserId123";

        fetch(`http://localhost:5000/api/stats/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                setUserStats(data);
            })
            .catch((error) =>
                console.error("Error fetching user stats:", error)
            );
    }, []);

    if (!userStats) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <Chrono startDate={userStats.startDate} />
            <div>
                <p>Money Saved: {userStats.moneySaved}â‚¬</p>
                <p>Life Gained: {userStats.lifeGained} hours</p>
            </div>
        </div>
    );
};

export default Dashboard;
