import React from "react";

const LifeGained = ({ cigarettesNotSmoked }) => {
    const minutesPerCigarette = 15; // Temps gagné par cigarette (en minutes)
    const totalMinutesGained = cigarettesNotSmoked * minutesPerCigarette;

    // Conversion en jours, heures, minutes
    const days = Math.floor(totalMinutesGained / (24 * 60));
    const hours = Math.floor((totalMinutesGained % (24 * 60)) / 60);
    const minutes = totalMinutesGained % 60;

    return (
        <div>
            <h3>Temps de vie gagné :</h3>
            <p>
                {days} jours, {hours} heures, {minutes} minutes
            </p>
        </div>
    );
};

export default LifeGained;
